mapper.select:
select id as id , job_id as jobId, day_count as dayCount, day_repay_count as dayRepayCount, all_count as allCount, all_repay_count as allRepayCount, create_time as createTime from asset_kefu_census where 1=1
and DATE_FORMAT(create_time, '%Y%m%d') >= DATE_FORMAT(#{createTime}, '%Y%m%d')
and job_id = #{jobId}

SELECT count( kc.id ) AS count FROM asset_kefu_census kc LEFT JOIN back_user bu ON bu.id = kc.job_id WHERE 1 =1
and DATE_FORMAT(kc.create_time, '%Y%m%d') >= DATE_FORMAT(#{beginTime}, '%Y%m%d') and DATE_FORMAT(kc.create_time, '%Y%m%d') <= DATE_FORMAT(#{endTime}, '%Y%m%d')
and bu.user_name = #{userName}

SELECT count( DISTINCT ( ba.borrow_order_id ) ) AS dayCount FROM asset_borrow_assign ba LEFT JOIN asset_borrow_order bo ON bo.id = ba.borrow_order_id LEFT JOIN asset_repayment ar ON bo.id = ar.asset_order_id LEFT JOIN asset_renewal_record rr ON rr.asset_repayment_id = ar.id WHERE ba.del_flg = 0 and rr.status = 1
AND ba.job_id = #{jobId}
and DATE_FORMAT( ba.create_time, '%Y%m%d') <= DATE_FORMAT(now(), '%Y%m%d') and DATE_FORMAT( rr.old_repayment_time, '%Y%m%d') <= DATE_FORMAT(now(), '%Y%m%d');
and DATE_FORMAT( ba.create_time, '%Y%m%d') = DATE_FORMAT(now(), '%Y%m%d') and DATE_FORMAT(rr.old_repayment_time, '%Y%m%d') = DATE_FORMAT(now(), '%Y%m%d');
