SELECT id, repay_date, expire_count, expire_money, repay_count, repay_money, extend_count, extend_money, extend_product_money, amortization_loan_count, amortization_loan_money, ove_repay_count, ove_repay_money, ove_wait_count, ove_wait_money, repay_rate, re_repay_rate, ove_first_rate, two_rate, three_rate, seven_rate, ove_rate, update_date FROM back_loan_census where 1=1
and repay_date >= #{startTime}
and repay_date <= #{endTime}
order by repay_date DESC

     select count(1) from back_loan_census
      where 1=1

		and repay_date >= #{startTime}


		and repay_date <= #{endTime}

		order by repay_date DESC

SELECT count( bo.id ) AS count, IFNULL(sum( bo.money_amount ),0) money FROM asset_borrow_order bo LEFT JOIN asset_repayment ar ON bo.id = ar.asset_order_id WHERE DATE_FORMAT( ar.repayment_time, '%Y-%m-%d' ) = #{repayTime}
and bo.status in (-11,-20)
and IFNULL( DATEDIFF( DATE_FORMAT( ar.repayment_real_time, '%Y-%m-%d' ), DATE_FORMAT( ar.repayment_time, '%Y-%m-%d' ) ), 0 ) = #{repayTime}
