mapper.select:
select

from borrow_product_config
where borrow_amount = #{borrowAmount} and borrow_day = #{borrowDay}

select

from borrow_product_config
where borrow_day = #{borrowDay,jdbcType=INTEGER}
and borrow_amount  <=  #{nowLimit,jdbcType=DECIMAL}
order  by borrow_amount desc
limit 1

SELECT a.id AS productId, a.product_name as productName, borrow_amount AS borrowAmount, total_fee_rate AS totalFeeRate, late_fee AS lateFee, borrow_day AS borrowDay, b.extend_name as extendName, b.extend_count as extendCount, b.extend_money as extendMoney, b.extend_status as extendStatus, c.limit_name as limitName, c.limit_count as limitCount, c.limit_product_id as limitProductId, (select borrow_amount from borrow_product_config where id = c.limit_product_id ) as limiitProductName, c.limit_status as limitStatus, a.status as status, a.borrow_day as borrowDay, a.extend_id as extendId, a.limit_id as limitId
FROM borrow_product_config a left join back_extend b on a.extend_id = b.id LEFT JOIN back_limit c on c.id = a.limit_id WHERE deal_flag = 'n'
and a.id = #{productId}
and borrow_amount = #{borrowAmount}
and a.status=#{status}
and product_name = #{productName}

SELECT
 count(a.id)
FROM
borrow_product_config  a
left join back_extend  b on a.extend_id = b.id
LEFT JOIN back_limit c on c.id = a.limit_id
WHERE
deal_flag = 'n'

  and a.id = #{productId}


  and borrow_amount = #{borrowAmount}


  and a.status=#{status}


  and product_name = #{productName}

select id,extend_name,extend_count,extend_money,extend_status,remark,extend_day from back_extend
where 1=1

   and extend_name =#{extendName}


  and extend_day = #{extendDay}

select count(id) from back_extend where 1=1

  and extend_name =#{extendName}


    and extend_day = #{extendDay}

select  id as id,limit_name as limitName,limit_count as limitCount,limit_product_id as limitProductId,(select product_name from borrow_product_config
where id= limit_product_id)as limiitProductName,limit_status as limitStatus,limit_remark as limitRemark from back_limit
where 1=1 and id = #{id}

select id as id,limit_name as limitName,limit_count as limitCount,limit_product_id as limitProductId,(select product_name from borrow_product_config
where id= limit_product_id)as limiitProductName,limit_status as limitStatus,limit_remark as limitRemark from back_limit
where 1=1

  and limit_name = #{limitName}


  and limit_count = #{limitCount}

select count(id) from back_limit
where 1=1

  and limit_name = #{limitName}


  and limit_count = #{limitCount}
