mapper.select:
select

from

where id = #{id,jdbcType=INTEGER}

	SELECT COUNT(1) from asset_borrow_order b  LEFT JOIN user_info u ON u.id=b.user_id WHERE u.user_from=#{channelId}

 		AND DATE_FORMAT(u.create_time,'%Y-%m-%d')=#{registeredSTime} AND DATE_FORMAT(u.create_time,'%Y-%m-%d')<=#{registeredETime}


 		AND DATE_FORMAT(u.create_time,'%Y-%m-%d')>=#{registeredSTime}


 		AND DATE_FORMAT(u.create_time,'%Y-%m-%d')<=#{registeredETime!=}



 		AND DATE_FORMAT(b.loan_time,'%Y-%m-%d')>=#{lendingSTime} AND DATE_FORMAT(b.loan_time,'%Y-%m-%d')<=#{lendingETime}


 		AND DATE_FORMAT(b.loan_time,'%Y-%m-%d')>=#{lendingSTime}


 		AND DATE_FORMAT(b.loan_time,'%Y-%m-%d')<=#{lendingETime}

	select u.create_time,u.user_name,b.money_amount,b.loan_term,b.created_at,b.loan_time,b.loan_end_time,b.status  from asset_borrow_order b  LEFT JOIN user_info u ON u.id=b.user_id WHERE u.user_from=#{channelId}

 		AND DATE_FORMAT(u.create_time,'%Y-%m-%d')=#{registeredSTime} AND DATE_FORMAT(u.create_time,'%Y-%m-%d')<=#{registeredETime}


 		AND DATE_FORMAT(u.create_time,'%Y-%m-%d')>=#{registeredSTime}


 		AND DATE_FORMAT(u.create_time,'%Y-%m-%d')<=#{registeredETime!=}



 		AND DATE_FORMAT(b.loan_time,'%Y-%m-%d')>=#{lendingSTime} AND DATE_FORMAT(b.loan_time,'%Y-%m-%d')<=#{lendingETime}


 		AND DATE_FORMAT(b.loan_time,'%Y-%m-%d')>=#{lendingSTime}


 		AND DATE_FORMAT(b.loan_time,'%Y-%m-%d')<=#{lendingETime}

mapper.update:
UPDATE channel_info
SET
channel_url = #{channelUrl},
promotion_url = #{promotionUrl}
WHERE id = #{id};
