mapper.select:
SELECT id, repay_time, new_loan_count, new_repay_count, old_loan_count, old_repay_count, extend_count, new_loan_money, new_repay_money, old_loan_money, old_repay_money, extend_money, extend_product_money, update_time FROM channel_ove_census where 1=1
and DATE_FORMAT( repay_time, '%Y-%m-%d' ) = #{repayTime}
and channel_id = #{channelId}

select count(1) from channel_ove_census
where 1=1

	and DATE_FORMAT( repay_time, '%Y-%m-%d' ) = #{repayTime}

SELECT ci.channel_name as channelName, si.channel_super_name as channelSuperName, si.id as channelSuperId, oc.id as id , oc.repay_time as repayTime, oc.new_loan_count as newLoanCount, oc.new_repay_count as newRepayCount, oc.old_loan_count as oldLoanCount, oc.old_repay_count as oldRepayCount, oc.extend_count as extendCount, oc.new_loan_money as newLoanMoney, oc.new_repay_money as newRepayMoney, oc.old_loan_money as oldLoanMoney, oc.old_repay_money as oldRepayMoney, oc.extend_money as extendMoney, oc.extend_product_money as extendProductMoney, oc.update_time as updateTime, oc.all_loan_count as allLoanCount, oc.all_repay_count as allRepayCount, oc.new_ove_rate as newOveRate, oc.old_ove_rate as oldOveRate, oc.all_ove_rate as allOveRate FROM channel_ove_census oc left join channel_info ci on oc.channel_id = ci.id left join channel_super_info si on si.id = ci.channel_super_id where 1=1 and oc.repay_time != DATE_FORMAT(now(), '%Y-%m-%d')
and si.id = #{superChannelId}
and ci.channel_name = #{channelName}
and DATE_FORMAT( oc.repay_time, '%Y-%m-%d' ) >= DATE_FORMAT(#{beginTime}, '%Y-%m-%d'); and DATE_FORMAT( oc.repay_time, '%Y-%m-%d' ) >= DATE_FORMAT(#{endTime}, '%Y-%m-%d');
order by oc.repay_time DESC

select count(oc.id)
FROM
channel_ove_census oc
left join channel_info ci on oc.channel_id = ci.id
left join channel_super_info si on si.id = ci.channel_super_id
where 1=1
and  oc.repay_time  != DATE_FORMAT(now(), '%Y-%m-%d')

	and si.id = #{superChannelId}


	and ci.channel_name = #{channelName}


	and   DATE_FORMAT( oc.repay_time, '%Y-%m-%d' ) >= DATE_FORMAT(#{beginTime}, '%Y-%m-%d');
	and   DATE_FORMAT( oc.repay_time, '%Y-%m-%d' ) >= DATE_FORMAT(#{endTime}, '%Y-%m-%d');

order by oc.repay_time DESC

SELECT
	count(ar.id) AS count,
	IFNULL(sum(bo.money_amount),0) as money
FROM
	asset_repayment ar
	LEFT JOIN asset_borrow_order bo ON ar.asset_order_id = bo.id
	LEFT JOIN user_info ui ON ui.id = ar.user_id
WHERE
	ui.user_from = #{userFrom}
	AND DATE_FORMAT( ar.repayment_time, '%Y-%m-%d' ) = #{repayTime}

		AND bo.customer_type = #{customerType}


		and bo.status = #{status}
