mapper.sql:
from asset_renewal_record
where 1 = 1

    and asset_repayment_id = #{assetRepaymentId}


    and user_id = #{userId}


    and status = #{status}

mapper.select:
select rr.id, u.id as userId, u.realname as realname, u.user_phone as userPhone, u.customer_type as customerType, r.repayment_principal, r.repayment_interest, r.repayment_amount as repaymentAmount, r.repaymented_amount as repaymentedAmount, rr.sum_fee, r.credit_repayment_time as creditRepaymentTime, rr.old_repayment_time, rr.repayment_time, r.repayment_real_time as repaymentRealTime, rr.renewal_type as renewalType, rr.renewal_kind as renewalKind, rr.order_time as orderTime, r.renewal_count as renewalCount, r.status as repayStatus, rr.renewal_day from asset_renewal_record rr , user_info u, asset_repayment r where rr.user_id = u.id and rr.asset_repayment_id = r.id and rr.status in (1) and rr.return_money = 0
and u.user_phone = #{phone}
and rr.order_time >= #{orderTime}
and rr.order_time < #{orderTimeEndChangeEnd}
and rr.renewal_type = #{renewalType}

select count(1)
from asset_renewal_record rr ,
user_info u,
asset_repayment r
where rr.user_id = u.id
and rr.asset_repayment_id = r.id and rr.status in (1)


    and u.user_phone = #{phone}


    and rr.order_time >= #{orderTime}


    and rr.order_time < #{orderTimeEndChangeEnd}


    and rr.renewal_type = #{renewalType}


    and rr.renewal_kind = #{renewalKind}

select count(1)
from asset_renewal_record rd,
asset_repayment r,
user_info u
where rd.user_id = u.id and rd.asset_repayment_id=r.id and rd.status in (1)


    and rd.repayment_time >= #{repaymentTime}



     and DATE_FORMAT(rd.repayment_time,'%Y-%m-%d') <= #{repaymentTimeEnd}

select sum(sum_fee) as amount
from asset_renewal_record rd,
asset_repayment r,
user_info u
where rd.user_id = u.id and rd.asset_repayment_id=r.id and rd.status in (1)


    and rd.repayment_time >= #{repaymentTime}



     and DATE_FORMAT(rd.repayment_time,'%Y-%m-%d') <= #{repaymentTimeEnd}

select sum(rd.repayment_interest) as amount
from asset_renewal_record  rd,
user_info u,
asset_repayment r
 where rd.user_id = u.id and rd.asset_repayment_id = r.id and rd.status in (1)


    and rd.repayment_time >= #{repaymentTime}


     and rd.repayment_time <= #{repaymentTimeEnd}

select sum(renewal_fee) as amount
from asset_renewal_record  rd,
user_info u,
asset_repayment r
where rd.user_id = u.id and rd.asset_repayment_id = r.id and rd.status in (1)



    and rd.repayment_time >= #{repaymentTime}



     and DATE_FORMAT(rd.repayment_time,'%Y-%m-%d') <= #{repaymentTimeEnd}
