mapper.sql:
from report_repayment
where 1 = 1

      and report_date >= #{reportDateStart}   


      and report_date <= #{reportDateEnd}

mapper.select:
SELECT re.id, re.user_id AS userId, rr.repayment_time AS repaymentTime, re.repayment_time AS repaymentTimeRe, re.first_repayment_time AS firstRepaymentTime, rr.money_amount AS moneyAmount, re.`status`, re.late_day AS lateDay, rr.renewal_day AS loanTerm FROM asset_renewal_record rr LEFT JOIN asset_repayment re ON re.id = rr.asset_repayment_id WHERE 1 = 1
and rr.repayment_time between str_to_date(#{firstRepaymentTime},'%Y-%m-%d') and date_add(#{firstRepaymentTime}, interval 1 day)
and rr.first_repayment_time <= #{firstRepaymentTimeEnd}
AND rr.`status` = 1;
